import React, { useContext } from 'react';
import { LanguageContext } from './LanguageContext';
import './LanguageSwitcher.css'

const LanguageSwitcher = () => {
    const {currentLang, handleClick, handleOnMouseEnter, handleOnMouseLeave, isActive } = useContext(LanguageContext);

    return (
        <div className='languageContainer'>
            <div className={`langSwitcher ${isActive ? 'switchModifier':''}`} onMouseEnter={handleOnMouseEnter} onMouseLeave={handleOnMouseLeave}>
                <span className={`currentLang `}>{currentLang}</span>
                {isActive && (<div className='langDropdown'>
                    <div className='language' onClick={() => handleClick("en")}>
                        <span className='languageText' onClick={() => handleClick("en")}>EN</span>
                    </div>
                    <div className='language' onClick={() => handleClick("ru")}>
                        <span className='languageText' onClick={() => handleClick("ru")}>RU</span>
                    </div>
                </div>
                )}
            </div>


        </div>
    )
}

export default LanguageSwitcher;
