import React, { useState, useEffect } from "react";
import { useLocation, useSearchParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { service } from "../../api/service";
import PostersTemplate from "../PostersTemplate/PostersTemplate";
import Pagination from "../Pagination/Pagination";
import NavBar from "../NavBar/NavBar";
import styles from "./Movies/Movies.module.css";

const SearchPage = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [searchParams, setSearchParams] = useSearchParams();
  const [totalResults, setTotalResults] = useState(0);
  const { t } = useTranslation();
  const query = searchParams.get("query");
  const url = `https://api.themoviedb.org/3/search/movie?query=${query}&language=${t(
    "api-code"
  )}`;

  useEffect(() => {
    service(url, "GET").then(({ data }) => {
      setSearchResults(data.results);
      setTotalResults(data.total_results);
    });
    setSearchParams({ query: query, page: searchParams.get("page") });
  }, [searchParams]);

  const handleBackPage = () =>
    setSearchParams({ page: searchParams.get("page") - 1 });

  const handleNextPage = () =>
    setSearchParams({ page: searchParams.get("page") + 1 });

  useEffect(() => {
    setSearchParams({ query: searchParams.get("query"), page: 1 });
  }, [query]);

  return (
    <>
      {searchResults ? (
        <div className={styles.moviesContainer}>
          {" "}
          {searchResults.map((movies) => (
            <PostersTemplate postersData={movies} type={"moviePosters"} />
          ))}
        </div>
      ) : (
        <h1>404 Not Found</h1>
      )}
      {totalResults >= 20 ? (
        <Pagination
          handleBackPage={handleBackPage}
          handleNextPage={handleNextPage}
        />
      ) : null}
    </>
  );
};
export default SearchPage;
