import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate} from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './Search.css';

const Search = () => {

    const { t } = useTranslation();
    const [searchParams, setSearchParams] = useSearchParams();
    const [inputValue, setInputValue] = useState("");
    const currPage = 1;
    const navigate = useNavigate();


    const handleChange = (event) => {
        setInputValue(event.target.value);
    }

    const handleSubmit = (event) => {
      event.preventDefault();
        setSearchParams( { "query": inputValue ? inputValue: ''});
      navigate(`/search?query=${inputValue}`, {replace: true})
    }

    return (
        <form className='navbar__searchBar' onSubmit={handleSubmit}>

                <label className='searchBar' htmlFor="focusing" id="focus">
                    <input 
                        id="focusing" 
                        className='searchBar__input' 
                        placeholder={t("searchBarPlaceholder")}
                        name='searchInput'
                        value={inputValue}
                        onChange={handleChange} 
                        autoComplete={'off'}/>
                    <input 
                        type='submit' 
                        className={`searchBar__button `} 
                        value={`${t("searchBarButton")}`}
                        id='searchButton'/>
                </label>
        </form>
    )
}

export default Search
