import React, { useState, useEffect, useContext} from 'react';
import { useParams} from 'react-router-dom';
import { service } from '../../api/service';
import { sliceDescription } from '../../utils/sliceDescription';
import { convertToDollars } from '../../utils/converToDollars';
import { convertToHours } from '../../utils/convertToHours';
import NavBar from '../NavBar/NavBar';
import Image from '../Image/Image';
import Rating from '../Rating/Rating';
import MovieImages from '../MovieImages/MovieImages'
import MovieActors from '../MovieActors/MovieActors';
import Recommendations from '../Recommendations/Recommendations';
import { useTranslation } from 'react-i18next';
import { LanguageContext } from '../LanguageSwitcher/LanguageContext';

import styles from  './Movie.module.css';

const Movie = () => {
    const { id } = useParams();
    const [movieDetails, setMovieDetails] = useState([]);
    const { t, i18n } = useTranslation();

    useEffect(()=>  {
        service(`https://api.themoviedb.org/3/movie/${id}?language=${t('api-code')}`, 'GET').then((details) => setMovieDetails(details.data))
    }, [id, i18n.language])

    const totalHours = convertToHours(movieDetails.runtime);

    return (
        <>
                <div className={styles.movieSingle}>
                    <div className={styles.singlePoster}>
                        <Rating rating={movieDetails.vote_average}/>
                        <div className={styles.posterImage}>
                            <Image image={movieDetails.poster_path}/>
                        </div>
                    </div>
                    <div className={styles.singleDetails}>
                        <div className={styles.details}>
                            <div className={styles.movieTitle}>
                                <span className={styles.titleContent}>{t("title")}:</span>
                                <span className={styles.titleText}>{movieDetails.title}</span>
                            </div>
                            <div className={styles.movieOverview}>
                                <span className={styles.itemContent}>{t("description")} </span>
                                <span className={styles.itemText}>{sliceDescription(movieDetails.overview)}</span>
                            </div>
                            <div className={styles.movieItem}>
                                <span className={styles.itemContent}>{t("release_date")}: </span>
                                <span className={styles.itemText}>{movieDetails.release_date}</span>
                            </div>
                            <div className={styles.movieItem}>
                                <span className={styles.itemContent}>{t("budget")}: </span>
                                <span className={styles.itemText}>$ {convertToDollars(movieDetails.budget)}</span>
                            </div>
                            <div className={styles.movieItem}>
                                <span className={styles.itemContent}>{t("revenue")}: </span>
                                <span className={styles.itemText}>$ {convertToDollars(movieDetails.revenue)}</span>
                            </div>
                            <div className={styles.movieItem}>
                                <span className={styles.itemContent}>{t("duration")}: </span>
                                <span className={styles.itemText}>{`${totalHours.hours}${t("hours")} ${totalHours.minutes}${t("minutes")}`}</span>
                            </div>
                            <div className='movieGenres'>
                                {
                                    movieDetails.genres ? (
                                        movieDetails.genres.map(({name}) => (<div className={styles.Genres}>
                                                <span>{name}</span>
                                            </div>))): null}
                            </div>
                            <MovieActors movie_id={id}/>
                            <MovieImages movie_id={id}/>
                        </div>
                    </div>

                </div>
                <div className={styles.recommendations}>
                    <Recommendations movie_id={id}/>
                </div> 
        </>
    )
}

export default Movie
