import { useState, createContext} from 'react';
import { useTranslation } from 'react-i18next';

export const LanguageContext = createContext();

export const LanguageProvider = ( {children} ) => {
    const [ isActive, setIsActive ]  = useState(false);
    const { i18n } = useTranslation();
    const [ currentLang, setCurrentLang] = useState(i18n.language.toUpperCase());

    const handleOnMouseEnter = () => setIsActive(true);
    const handleOnMouseLeave = () => setIsActive(false);

    const handleClick = (language) => {
        if(language === "en") {
            setCurrentLang("EN");
            i18n.changeLanguage("en")
        }
        else if(language === "ru"){
            setCurrentLang("RU");
            i18n.changeLanguage("ru")
        }
    }

    return (
        <LanguageContext.Provider value={{currentLang, handleClick, handleOnMouseEnter, handleOnMouseLeave, isActive}}>
            {children}
        </LanguageContext.Provider>
    )

}
