import React, { useState, useEffect, useContext} from 'react';
import { useParams} from 'react-router-dom';
import { service } from '../../api/service';
import { sliceDescription } from '../../utils/sliceDescription';
import { convertToDollars } from '../../utils/converToDollars';
import { convertToHours } from '../../utils/convertToHours';
import NavBar from '../NavBar/NavBar';
import Image from '../Image/Image';
import Rating from '../Rating/Rating';
import MovieImages from '../MovieImages/MovieImages'
import MovieActors from '../MovieActors/MovieActors';
import Recommendations from '../Recommendations/Recommendations';
import { useTranslation } from 'react-i18next';
import { LanguageContext } from '../LanguageSwitcher/LanguageContext';

import styles from  './Movie.module.css';

const Movie = () => {
    const { id } = useParams();
    const [movieDetails, setMovieDetails] = useState([]);
    const { t, i18n } = useTranslation();

    useEffect(()=>  {
        service(`https://api.themoviedb.org/3/movie/${id}?language=${t('api-code')}`, 'GET').then((details) => setMovieDetails(details.data))
    }, [id, i18n.language])

    const totalHours = convertToHours(movieDetails.runtime);

    return (
        <>
            <div className={styles.wrapMain}>
                <div className={styles.movieSingle}>
                    <div className='singlePoster'>
                        <Rating rating={movieDetails.vote_average}/>
                        <div className='posterImage'>
                            <Image image={movieDetails.poster_path}/>
                        </div>
                    </div>
                    <div className='singleDetails'>
                        <div className='details'>
                            <div className='movieTitle'>
                                <span className='titleContent'>{t("title")}:</span>
                                <span className='titleText'>{movieDetails.title}</span>
                            </div>
                            <div className='movieOverview'>
                                <span className='itemContent'>{t("description")} </span>
                                <span className='itemText'>{sliceDescription(movieDetails.overview)}</span>
                            </div>
                            <div className='movieItem'>
                                <span className='itemContent'>{t("release_date")}: </span>
                                <span className='itemText'>{movieDetails.release_date}</span>
                            </div>
                            <div className='movieItem'>
                                <span className='itemContent'>{t("budget")}: </span>
                                <span className='itemText'>$ {convertToDollars(movieDetails.budget)}</span>
                            </div>
                            <div className='movieItem'>
                                <span className='itemContent'>{t("revenue")}: </span>
                                <span className='itemText'>$ {convertToDollars(movieDetails.revenue)}</span>
                            </div>
                            <div className='movieItem'>
                                <span className='itemContent'>{t("duration")}: </span>
                                <span className='itemText'>{`${totalHours.hours}${t("hours")} ${totalHours.minutes}${t("minutes")}`}</span>
                            </div>
                            <div className='movieGenres'>
                                {
                                    movieDetails.genres ? (
                                        movieDetails.genres.map(({name}) => (<div className='Genres'>
                                                <span>{name}</span>
                                            </div>))): null}
                            </div>
                            <MovieActors movie_id={id}/>
                            <MovieImages movie_id={id}/>
                        </div>
                    </div>

                </div>
                <div className='recommendations'>
                    <Recommendations movie_id={id}/>
                </div>
            </div>

        </>
    )
}

export default Movie
