import React, { useState, useEffect } from "react";
import { useLocation, useSearchParams} from "react-router-dom";
import { useTranslation } from "react-i18next";
import { service } from "../../api/service";
import PostersTemplate from "../PostersTemplate/PostersTemplate";
import Pagination from "../Pagination/Pagination";
import NavBar from "../NavBar/NavBar";
import styles from "./Movies/Movies.module.css";

const SearchPage = () => {
    const [searchResults, setSearchResults] = useState([]);
    const [searchParams, setSearchParams] = useSearchParams();
    const [currPage, setCurrPage] = useState(1);
    const [totalResults, setTotalResults]= useState(0);
    const { t } = useTranslation();
    const query = searchParams.get("query")

    useEffect(() => {
        service(`https://api.themoviedb.org/3/search/movie?query=${query}&language=${t('api-code')}&page=${currPage}`, 'GET').then(({data}) => {
            setSearchResults(data.results)
            setTotalResults(data.total_results)});
        },[searchParams, currPage])

    const handleBackPage = () => setCurrPage(currPage - 1);

    const handleNextPage = () => setCurrPage(currPage + 1);

    useEffect(() => {
        setCurrPage(1)
    }, [query])

    return (
        <>
            {searchResults ? (<div className={styles.moviesContainer}> {searchResults.map(movies => <PostersTemplate postersData={movies} type={"moviePosters"} />)}</div>):(<h1>404 Not Found</h1>)}
            {totalResults >= 20 ? (<Pagination handleBackPage={handleBackPage} handleNextPage={handleNextPage} currPage={currPage} />):null }
        </>)}
export default SearchPage;
