import React from 'react';
import styles from './Rating.module.css';

const Rating = ({rating, type}) => {
    return (
        type === 'moviePosters' ? (
            <div className={`rating ${rating >= 7 ? 'ratingPositive' : 'ratingNegative'}`}>
                <span className='ratingText'>{rating.toFixed(1)}</span>
            </div>):(
            <div className={`ratingMovieDetails ${rating >= 7 ? 'ratingPositive' : 'ratingNegative'}`}>
                <span className='ratingMovieDetailsText'>{rating}</span>
            </div>))}
export default Rating;
