import React, { useState, useEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import { useQuery } from "@tanstack/react-query";
import { useTranslation } from "react-i18next";
import { service } from "../../api/service";
import PostersTemplate from "../../components/PostersTemplate/PostersTemplate";
import Pagination from "../../components/Pagination/Pagination";
import NavBar from "../../components/NavBar/NavBar";
import styles from "../Movies/Movies.module.css";

const SearchPage = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [searchParams, setSearchParams] = useSearchParams();
  const [totalResults, setTotalResults] = useState(0);
  const navigate = useNavigate();
  const { t } = useTranslation();
  const query = searchParams.get("query");

  // useEffect(() => {
  //   service(
  //     `https://api.themoviedb.org/3/search/movie?query=${query}&language=${t(
  //       "api-code"
  //     )}&page=${searchParams.get("page") ? searchParams.get("page") : 1}`,
  //     "GET"
  //   ).then(({ data }) => {
  //     setSearchResults(data.results);
  //     setTotalResults(data.total_results);
  //   });
  //   setSearchParams({
  //     query: query,
  //     page: searchParams.get("page") ? searchParams.get("page") : 1,
  //   });
  // }, [searchParams]);

  const { data, isError, isSuccess, isFetched, error } = useQuery({
    queryKey: ["SearchPage", searchParams],
    queryFn: async () => {
      const { data } = await service(
        `https://api.themoviedb.org/3/search/movie?query=${query}&language=${t(
          "api-code"
        )}&page=${searchParams.get("page") ? searchParams.get("page") : 1}`,
        "GET"
      );
      setSearchParams({
        query: query,
        page: searchParams.get("page") ? searchParams.get("page") : 1,
      });
      return data;
    },
  });

  const handleBackPage = () =>
    setSearchParams({
      query: searchParams.get("query"),
      page: searchParams.get("page") - 1,
    });

  const handleNextPage = () =>
    setSearchParams({
      query: searchParams.get("query"),
      page: parseInt(searchParams.get("page", 10)) + 1,
    });

  useEffect(() => {
    setSearchParams({
      query: searchParams.get("query"),
      page: navigate(-1) || 1,
    });
  }, [query]);

  return (
    <>
      {isSuccess && (
        <div className={styles.moviesContainer}>
          {" "}
          {data.map((movies) => (
            <PostersTemplate
              postersData={movies}
              type={"moviePosters"}
              key={movies.id}
            />
          ))}
        </div>
      )}
      {
        <Pagination
          handleBackPage={handleBackPage}
          handleNextPage={handleNextPage}
          searchResults={searchResults}
        />
      }
    </>
  );
};
export default SearchPage;
