import React, { useState, useEffect, useContext } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { service } from "../../../api/service";
import { Link } from "react-router-dom";
import { useTranslation } from "react-i18next";
import Rating from "../../Rating/Rating"
import Image from "../../Image/Image";
import Genres from "../../Genres/Genres";
import PostersTemplate from "../../PostersTemplate/PostersTemplate";
import Pagination from "../../Pagination/Pagination";
import styles from "./Movies.module.css"

const Movies = () => {
    const [moviesData, setMoviesData] = useState([]);
    const [searchParams] = useSearchParams();
    const [currPage, setCurrPage] = useState(1);
    const [totalResults, setTotalResults] = useState(0);
    const { t, i18n } = useTranslation();
    let totalPages;
    const navigate = useNavigate();


    useEffect(() => {
        service(`https://api.themoviedb.org/3/movie/popular?language=${t("api-code")}&page=${currPage}`,"GET").then(({data}) => {
          console.log(data)
          setMoviesData(data.results); //data.results equal to 20, then content per page will be 20;
          setTotalResults(data.total_results)
          totalPages = data.total_pages;
        });
        navigate(`/?page=${currPage}` ,{replace: true}) 
      }, [ i18n.language, currPage , navigate])

      const handleBackPage = () => {
        if (currPage > 1) {
          setCurrPage(currPage - 1);}}

      const handleNextPage = () => {
        if (currPage < totalPages) {
          setCurrPage(currPage + 1);}}

      return (
        <>
          <div className={styles.moviesContainer}>
              {moviesData.map((movies) => <div className={styles.movies} key={movies.id}>
                <PostersTemplate postersData={movies} type={"moviePosters"}/>
                </div>)}
          </div>

          {totalResults > moviesData.length ? (<Pagination handleBackPage={handleBackPage} handleNextPage={handleNextPage} currPage={currPage} />) : null}
        </>
    )
}

export default Movies;
