import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { service } from '../../api/service';
import { useTranslation } from 'react-i18next';
import NavBar from '../NavBar/NavBar';
import Image from '../Image/Image';
import ActorImages from '../ActorImages/ActorImages';
import ActorCredits from '../ActorCredits/ActorCredits';
import styles from './Actor.module.css';


const Actor = () => {
    const [ actorDetails, setActorDetails ] = useState([])
    const { id } = useParams();
    const { t, i18n } = useTranslation();

    useEffect(() =>{
        service(`https://api.themoviedb.org/3/person/${id}?language=${t("api-code")}`, 'GET').then(item => setActorDetails(item.data))
    }, [id, i18n.language])

    return (
        <>
                <div className={styles.profilePage}>
                    <div className='profilePicture'>
                        <Image image={actorDetails.profile_path} />
                    </div>
                    <div className='profileDetails'>
                        <span className='detailsName'>{actorDetails.name}</span>
                        <div className='detailsItem'>
                            <span className='itemPrefix'>{t("birthday")}: </span>
                            <span className='itemValue'>{actorDetails.birthday}</span>
                        </div>
                        <div className='detailsItem'>
                            <span className='itemPrefix'>{t("placeOfBirth")}: </span>
                            <span className='itemValue'>{actorDetails.place_of_birth}</span>
                        </div>
                        {actorDetails.biography && (
                            <div className='detiailsBiography'>
                                <span className='itemPrefix'>{t("biography")}: </span>
                                <span className='biographyValue'>{actorDetails.biography}</span>
                            </div>)}
                        <ActorImages person_id={id}/>
                    </div> 
                </div>
                <ActorCredits person_id={id} />
        </>
    )
}

export default Actor
