import React, { useState} from 'react';
import { useTranslation } from 'react-i18next';
import styles from './LanguageSwitcher.module.css';

const LanguageSwitcher = ({handleOnMouseEnter, handleOnMouseLeave, handleClick}) => {
    const [ isActive, setIsActive ]  = useState(false);
    const { i18n } = useTranslation();

    return (
        <div className={styles.languageContainer}>
            <div className={`${styles.langSwitcher} ${isActive ? styles.switchModifier:''}`} onMouseEnter={handleOnMouseEnter} onMouseLeave={handleOnMouseLeave}>
                <span className={styles.currentLang }>{i18n.language}</span>
                {isActive && (<div className={styles.langDropdown}>
                    <div className={styles.language} onClick={() => handleClick("en")}>
                        <span className={styles.languageText} onClick={() => handleClick("en")}>EN</span>
                    </div>
                    <div className={styles.language} onClick={() => handleClick("ru")}>
                        <span className={styles.languageText} onClick={() => handleClick("ru")}>RU</span>
                    </div>
                </div>)}
            </div>
        </div>)}
export default LanguageSwitcher;
